import React, { useState } from 'react';
import './App.css';
import axios from 'axios';  // Import Axios for making HTTP requests

const App = () => {
  const [vote, setVote] = useState(null);
  const [error, setError] = useState(null);

  // Handle the vote submission
  const handleVote = (choice) => {
    setVote(choice);  // Set the voted choice on the frontend

    // Send the vote to the backend using Axios
    axios
      .post('http://localhost:5000/vote', { choice })  // Ensure the correct backend URL
      .then((response) => {
        console.log(response.data);
        setError(null);  // Clear any previous errors if the vote was successful
      })
      .catch((error) => {
        console.error('There was an error!', error);
        setError('Error: Could not submit vote.');  // Set the error message if there’s an issue
      });
  };

  return (
    <div className="App">
      <h1>Voting App</h1>
      <div>
        <button onClick={() => handleVote('Choice 1')}>Vote for Choice 1</button>
        <button onClick={() => handleVote('Choice 2')}>Vote for Choice 2</button>
      </div>

      {vote && <p>You voted for: {vote}</p>}  {/* Display the voted choice */}

      {error && <p style={{ color: 'red' }}>{error}</p>}  {/* Display any error */}
    </div>
  );
};

export default App;
